const socket=io();let name,textarea=document.querySelector("#textarea"),messagearea=document.querySelector(".message__area");function sendMessage(e){let t={user:name,message:e.trim()};appendMessage(t,"outgoing"),textarea.value=" ",socket.emit("message",t)}function appendMessage(e,t){let s=document.createElement("div"),n=t;s.classList.add(n,"message");let a=`\n   <h4>${e.user}</h4>\n   <p>${e.message}</p>\n`;s.innerHTML=a,messagearea.appendChild(s),scrolltobottom()}function scrolltobottom(){messagearea.scrollTop=messagearea.scrollHeight}name="tejas",textarea.addEventListener("keyup",(function(e){if("Enter"==e.key)return sendMessage(e.target.value)})),socket.on("user__joined",(e=>{console.log(e),appendMessage(e,"incoming"),scrolltobottom()}));const up_down_btn=document.querySelector("#show_button");up_down_btn.addEventListener("click",(function(){document.querySelector(".chat__section").classList.toggle("hidden")}));